import { useState } from "react"

const AnimalsPage = (props) =>{
    console.log(props)
    console.log(typeof(props.vocabs[0].english_to_chinese))
    console.log(props.vocabs[0].english_to_chinese)
    const [englishLanguage, setEnglishLanguage] = useState('True')

    const handleLanguageChange = (props) =>{
      setEnglishLanguage = 'False'
      props.vocab.english_to_chinese = setEnglishLanguage
    }

    let animalVocabs = []
    for ( let i = 0; i <props.vocabs.length; i++) { 
      if(props.vocabs[i].category === 'animal'){
          animalVocabs.push(props.vocabs[i])
      }
    }



    console.log(animalVocabs)

    return(
      <>
      <h1>VOCAB CARDS</h1>
      {animalVocabs.map((vocab) => {     
        console.log(vocab)   
          return (
            <div key={vocab.id}>
              <div>REMEMBER?</div>                               
              <div>          
              {vocab.vocab_english}
              </div>
              <button onClick={handleLanguageChange}>Click Me to See the Answer</button>                                             
            </div>
          )
        } )}
      </>
    )

}







export default AnimalsPage;
