const AnimalsPage = (props) =>{
    console.log(props)
    console.log(props.vocabs)
    const [language, setLanguage] = useState('english')

    let animalVocabs = []
    for ( let i = 0; i <props.vocabs.length; i++) { 
      if(props.vocabs[i].category === 'animal'){
          animalVocabs.push(props.vocabs[i])
      }
    }

    const handleClick =() => {
      setLanguage('chinese')
    }
     console.log(animalVocabs)
     console.log(language)



    if(language === 'english'){
      return(
      animalVocabs.map((vocab, i) => {
        return (
          <div key={vocab.id}>
            <div>REMEMBER?</div>                               
            <div> 
            {vocab.vocab_english}
            </div>
            <button>Click Me to See the Answer</button>                                             
          </div>
        )
      }))
    } 
}




export default AnimalsPage;
