import { useEffect, useState} from "react"
let baseUrl = 'http://localhost:8000/api/v1'
const AnimalsPage = () =>{
    // console.log(props)
    // console.log(typeof(props.vocabs[0].english_to_chinese))
    // console.log(props.vocabs[0].english_to_chinese)
    const [englishToggle, setEnglishToggle] = useState ('test')
    const [animalVocabs, setAnimalVocabs] = useState([])
    // const [vocabs, setVocabs]= useState([])
    // console.log(props.vocabs)
    // console.log(props.vocabs.length)


    useEffect(()=>{
      // console.log(props.vocabs)
      fetch(baseUrl + '/vocab/', {
        credentials: "include",
        headers: {
          'Content-Type': 'application/json'
        }
      })
      .then(res => {
        // console.log('hit vocab')
        if(res.status === 200){
          return res.json()
        } else {
          return []
        }
      }).then(data => {


        handleAnimalVocabs(data.data)
      })

    },[])

    const handleAnimalVocabs = (vocabs) =>{
      // console.log(vocabs)
      // console.log('i am in animal kindom')
      let animalArray = []
      for (let i = 0; i <vocabs.length; i++) {      
        if(vocabs[i].category === 'animal'){
          // console.log('here')
         animalArray.push(vocabs[i])        
        }
      }
      setAnimalVocabs(animalArray)      

    }    
    // console.log(animalVocabs)   

    const handleClick = (e) => {
      for (let i=0; i<animalVocabs.length; i++){
        console.log('here1')
        console.log(animalVocabs[i].id)
        console.log(e.target.id)
        if(true){
          console.log('here2')
          setEnglishToggle('test2')
          console.log(setEnglishToggle('test2'))
          animalVocabs[i].english_to_chinese = englishToggle
        }
        console.log(animalVocabs[i].id)
        console.log(animalVocabs[i].english_to_chinese)

      }          
    }

    return(
      <>

      <h1>VOCAB CARDS</h1>
      {animalVocabs.map((vocab) => {   
        return (
          <div key={vocab.id}>
            <div>REMEMBER?</div>                               
            <div>          
            {vocab.vocab_english}
            </div>
            <button id={vocab.id} onClick={handleClick}>Click Me to See the Answer</button>                                             
          </div>
        )
      }         
    )}
    </> 
  )      
}

export default AnimalsPage;
