import React, { useEffect, useState} from "react"
let baseUrl = 'http://localhost:8000/api/v1'
const AnimalsPage = () =>{

    const [englishToggle, setEnglishToggle] = useState(true)
    const [animalVocabs, setAnimalVocabs] = useState([])


    useEffect(()=>{
      // console.log(props.vocabs)
      fetch(baseUrl + '/vocab/', {
        credentials: "include",
        headers: {
          'Content-Type': 'application/json'
        }
      })
      .then(res => {
        // console.log('hit vocab')
        if(res.status === 200){
          return res.json()
        } else {
          return []
        }
      }).then(data => {


        handleAnimalVocabs(data.data)
      })

    },[])

    const handleAnimalVocabs = (vocabs) =>{
      // console.log(vocabs)
      // console.log('i am in animal kindom')
      let animalArray = []
      for (let i = 0; i <vocabs.length; i++) {      
        if(vocabs[i].category === 'animal'){
          // console.log('here')
         animalArray.push(vocabs[i])        
        }
      }
      setAnimalVocabs(animalArray)      

    }    
    // console.log(animalVocabs)   

    const handleClick = (e) => {
    //  setEnglishToggle('test2')
    //  console.log(englishToggle)
      for (let i=0; i<animalVocabs.length; i++){
        console.log('here1')
        console.log(animalVocabs[i].id)
        console.log(e.target.id)
        if(animalVocabs[i].id == e.target.id){
          console.log('here2')
          setEnglishToggle(false)      
          animalVocabs[i].english_to_chinese = englishToggle
        }      
      }          
    }


    return(
      <>
      <h1>VOCAB CARDS</h1>
      {animalVocabs.map((vocab) => {   
        // console.log(vocab.id)
        // console.log(vocab.english_to_chinese)
        return (
          <div key={vocab.id}>
            <div>REMEMBER?</div>                               
            <div>          
            {vocab.vocab_english}
            </div>
            <div>          
            {String(vocab.english_to_chinese)}
            </div>
            <div>          
            {String(vocab.english_to_chinese)}
            </div>
            <button id={vocab.id} onClick={()=> setEnglishToggle(false)}>Click Me to See the Answer</button>                                             
          </div>
        )
      }         
    )}
    </> 
  )      
}

export default AnimalsPage;
